import CustomValueFormat from '../enums/CustomValueFormat';
import CustomValue from './CustomValue';
/**
 * Represents a custom value of Number or Timer type.
 */
export default class CustomNumberValue implements CustomValue {
    readonly value: number;
    readonly format: CustomValueFormat;
    static decode(input: string, format: CustomValueFormat): CustomNumberValue;
    constructor(value: number, format?: CustomValueFormat);
    encode(): string;
}
